import React,{ useRef, useState, Fragment } from 'react'
import { Link } from 'react-router-dom'
import logo from '../../logo.jpg';
// eslint-disable-next-line 
import identification from '../identification';
import '../../styles.css';

const Header = () => {

    const [btn] = useState(false);

    const refWolverine = useRef(null);

    <header>
        <div class= "banner-container">
    <nav className="navbar">
        <ul>
            <li><a href="#home">Chess7</a></li>
            <li class="accueil-bouton"><a class="active" href="/identification" >identifier</a></li>
            <li class="accueil-bouton"><a class="active" href="/identification" >inscrire</a></li>
          </ul> 


        <img src={logo} className="App-logo" alt="logo" />
    </nav>
    </div>
    </header>
 const displayBtn = btn && (
            <Fragment>
                <div>
                    <Link className="btn-welcome" to="/identification">S'identifier</Link>
                </div>
                <div>
                    <Link className="btn-welcome" to="/identification">S'inscrire</Link>
                </div>
            </Fragment>
 )
        return (
            <main ref={refWolverine} className="homepage_body">
                { displayBtn }
            </main>
        )
}
export default Header
